from functools import reduce
import time
def find_substring(str):
    sub=''
    L = len(str)
    for i in range(L):
        l = len(sub)
        if l>L-i:
            return sub
        for j in range(i+2,L+1):
            s=str[i:j]
            if s==s[::-1]:
                if j-i > l:
                    sub=s
    if sub=='':
        sub=str[0]
    return sub
def main():
    #str=""
    str=""
    print(len(str))
    t=time.time()
    print(find_substring(str),time.time()-t)

if __name__ == '__main__':
    main()